mapper.select:
SELECT id AS nextId,article_title AS nextTitle
FROM article
WHERE (id <#{articleId}) LIMIT 1;

SELECT article.id, article.userid, article_title, article_abstract, create_date, edit_date, article_second_id,article_main_id, article_main_categary.name AS mname,article_second_categary.name AS cname, (SELECT COUNT(0) FROM thank WHERE thank.articleid=article.id) AS articleThank, (SELECT COUNT(0) FROM comment WHERE comment.articleid=article.id) AS articleComment, (SELECT COUNT(0) FROM collection WHERE collection.articleid=article.id) AS articleCollection FROM article INNER JOIN article_main_categary ON article.article_main_id = article_main_categary.id INNER JOIN article_second_categary ON article.article_second_id = article_second_categary.id
WHERE article.article_main_id=#{articleMid}
WHERE article.article_second_id=#{articleCid}
ORDER BY article.id DESC LIMIT #{startRow},#{pageSize};

mapper.update:
update article
